DynamicReport.filterConfig:
{
		  api: "/o/rest/statistics/dossier/summary",
		  exclusive: false,
		  customize: true,
		  groupBy: [
				{
				key: "govAgencyCode",
				label: "Cơ quan",
				display: "govAgencyName"
				}
			],
		  groupIds: [
		  ],
		  sumKey: "serviceCode",
		  filters: [
			{
			  label: "Mức độ",
			  key: "serviceLevel",
			  value: "3,4",
			  type: "select",
			  source: [
				{name: "Tất cả", value: "3,4"},
				{name: "Mức độ 3", value: "3"},
				{name: "Mức độ 4", value: "4"}
			  ]  
			},
			{
			  label: "Phát sinh hồ sơ ",
			  key: "hasDossier",
			  value: "1",
			  type: "select",
			  source: [
				{name: "Tất cả", value: "1"},
				{name: "Có phát sinh hồ sơ", value: "2"},
				{name: "Không phát sinh hồ sơ", value: "3"}
			  ]  
